import { Component, OnInit } from '@angular/core';
import { Event, NavigationEnd, Router } from '@angular/router';
import { DashboardService } from '../dashboard.service';

@Component({
  selector: 'dashboard-layout',
  templateUrl: './layout.component.html',
})
export class LayoutComponent implements OnInit {
  constructor(private router: Router, private dashboard: DashboardService) {
    this.router.events.subscribe((event: Event) => {
      this.dashboard.setCurrentRoute(this.router.url);
      if (event instanceof NavigationEnd) {
        if (this.dashboard.sidebarOpen) {
          this.dashboard.closeSidebar();
        }
      }
    });
  }

  ngOnInit() {
    // set the html tag attribute overflow to hidden when component is mounted
    document.documentElement.style.overflow = 'hidden';
  }
}
<div class="relative h-screen overflow-hidden bg-gray-100">
  <div class="flex items-start">
    <overlay />
    <sidebar mobileOrientation="end" />
    <!-- lg:w-[calc(100%-16rem)] class get the remained width of the main tag from lg:viewport by subtracting
(the total width by the width of the sidebar component which is w-64 = 16rem)-->
    <div
      class="flex h-screen w-full flex-col pl-0 lg:space-y-4 lg:pl-24"
    >
      <top-bar />
      <main
        class="h-screen overflow-auto px-2 pb-36 pt-4 md:pb-8 lg:px-4"
      >
        <ng-content />
      </main>
    </div>
  </div>
</div>
