import { Component, Input } from '@angular/core';
import { DashboardService } from '../../dashboard.service';

@Component({
  selector: 'sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css'],
})

export class SidebarComponent {
  isLogged: false = false;
  @Input() mobileOrientation: 'start' | 'end';
  styles: {
    start: string;
    end: string;
  };
  constructor(private dashboard: DashboardService) {
    this.mobileOrientation = 'end';
    this.styles = {
      start: 'left-0',
      end: 'right-0 lg:left-0',
    };
  }

  sidebarOpen() {
    return this.dashboard.sidebarOpen;
  }
}
<aside
  class="scrollbar top-0 h-screen overflow-y-auto bg-black lg:absolute lg:z-40 lg:block
{{ styles[mobileOrientation] }}
{{
    sidebarOpen()
      ? 'absolute z-40 w-8/12 transition-all duration-500 ease-in sm:w-5/12 md:w-64'
      : 'hidden transition-all duration-700 ease-out lg:w-24'
  }}"
>
  <div class="pb-32 lg:pb-12">
    <sidebar-header></sidebar-header>
    <sidebar-items *ngIf="isLogged"></sidebar-items>
  </div>
</aside>
